import React from "react";
import logo from "../assets/img/logo.jpg";

function Home() {
  return (
    <section className="bg-[#0f0f0f] text-white py-20 px-[10%] text-center">
      {/* Logo stays at the top */}
      <div className="flex justify-center mb-10">
        <img src={logo} alt="ConVINCE Logo" className="h-32 w-auto" />
      </div>

      {/* Title */}
      <h2 className="text-4xl text-[#ffcc70] mb-10">Welcome to ConVINCE Portfolio</h2>

      {/* Team Background and Purpose */}
      <div className="max-w-4xl mx-auto space-y-10">
        <div>
          <h3 className="text-2xl text-[#ffcc70] mb-2">Team Background</h3>
          <p className="text-gray-300 leading-relaxed">
            ConVINCE is a group of passionate BSIT students from Cavite State Universityâ€“Imus, united by our love for technology and collaboration. We specialize in various areas like web development, mobile apps, and system design, learning and growing together through real-world projects and peer-led workshops.
          </p>
        </div>

        <div>
          <h3 className="text-2xl text-[#ffcc70] mb-2">Team Purpose</h3>
          <p className="text-gray-300 leading-relaxed">
            Our purpose is to showcase our skills, foster continuous growth, deliver impactful solutions, and emphasize the power of teamwork. Through this portfolio, we aim to highlight our journey, creating meaningful digital products that solve real problems while learning and improving as a team.
          </p>
        </div>
      </div>
    </section>
  );
}

export default Home;
