import React from "react";
import { Link } from "react-scroll";

function Home() {
  return (
    <section id="home" className="relative bg-[#0f0f0f] text-white min-h-screen flex flex-col justify-center items-center">
      <div className="text-center relative">
        {/* Logo */}
        <img
          src="./img/logo.jpg"
          alt="Logo"
          className="w-64 h-64 mb-[-40px] mx-auto z-10"
        />
        {/* Welcome Text */}
        <h1 className="text-6xl font-bold text-[#ffcc70] relative z-0">Welcome to Our Portfolio</h1>

        <Link
          to="portfolio"
          smooth={true}
          duration={500}
          className="mt-10 inline-block bg-[#ffcc70] text-black px-6 py-3 rounded-lg font-bold hover:bg-[#e6b85e] transition cursor-pointer"
        >
          View Portfolio
        </Link>
      </div>
    </section>
  );
}

export default Home;
