import React from "react";

function Team({ profiles }) {
  return (
    <section id="team" className="py-20 px-[10%] bg-[#0f0f0f] text-center">
      <h2 className="text-4xl text-[#ffcc70] text-center mb-10">Our Team</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8 justify-center">
        {Object.keys(profiles).map((name, idx) => (
          <div key={idx} className="relative flex flex-col items-center">
            <img
              src={profiles[name].image}
              alt={name}
              className="w-40 h-52 border-8 border-[#333] shadow-lg bg-white relative"
            />
            <h3 className="mt-3 text-[#ffcc70] font-bold">{name}</h3>
            <p className="text-gray-300">{profiles[name].role.join(", ")}</p>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Team;
