import React, { useState } from "react";
import logo from "../assets/img/logo.jpg"; // Import the logo image

function TeamSection({ profiles }) {
  const [dragging, setDragging] = useState(false);
  const [draggedItem, setDraggedItem] = useState(null);
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [profilePositions, setProfilePositions] = useState({}); // Save positions here

  // Check if profiles data is available
  if (!profiles || Object.keys(profiles).length === 0) {
    return <p className="text-white">No team profiles available.</p>;
  }

  const handleMouseDown = (e, index) => {
    setDragging(true);
    const { clientX, clientY } = e;
    setDraggedItem(index);
    setOffset({
      x: clientX,
      y: clientY,
    });
  };

  const handleMouseMove = (e) => {
    if (!dragging || draggedItem === null) return;
    const { clientX, clientY } = e;
    const deltaX = clientX - offset.x;
    const deltaY = clientY - offset.y;

    // Update the dragged profile's position
    setProfilePositions((prevPositions) => {
      const updatedPositions = { ...prevPositions };
      updatedPositions[draggedItem] = {
        x: (updatedPositions[draggedItem]?.x || 0) + deltaX,
        y: (updatedPositions[draggedItem]?.y || 0) + deltaY,
      };
      return updatedPositions;
    });

    // Update the offset for the next mouse move
    setOffset({
      x: clientX,
      y: clientY,
    });
  };

  const handleMouseUp = () => {
    setDragging(false);
    setDraggedItem(null);
  };

  // Open the resume when clicking on the profile image
  const handleProfileImageClick = (resumeUrl) => {
    if (resumeUrl) {
      window.open(resumeUrl, "_blank"); // Open the resume URL in a new tab
    }
  };

  return (
    <section
      id="team"
      className="py-20 px-[10%] bg-[#0f0f0f] relative"
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
    >
      <h2 className="text-4xl text-[#ffcc70] text-center mb-10">Meet the Team</h2>
      <div className="flex justify-center items-center relative">
        {/* Custom Logo with Animation */}
        <img
          src={logo}
          alt="Team Logo"
          id="logo" // Apply custom logo styles here
          className="w-48 h-48 object-cover rounded-full absolute z-0 opacity-20 cursor-pointer"
        />
        {/* Profiles around the logo */}
        <div className="relative w-[700px] h-[700px]">
          {Object.entries(profiles).map(([name, profile], idx) => {
            const position = profilePositions[idx] || { x: 0, y: 0 };

            // Calculate angle for circular positioning
            const angle = (360 / Object.entries(profiles).length) * idx;
            const transform = `rotate(${angle}deg) translateX(300px) rotate(-${angle}deg)`; 

            return (
              <div
                key={idx}
                className="absolute cursor-pointer"
                style={{
                  transform: transform,
                  left: `${position.x}px`,
                  top: `${position.y}px`,
                }}
                onMouseDown={(e) => handleMouseDown(e, idx)}
              >
                <div
                  className="bg-[#1a1a1a] border border-[#333] rounded-lg overflow-hidden p-5 text-center transition-all duration-300 z-10"
                  style={{
                    width: "400px", // Increased card width
                    height: "500px", // Increased card height for more content
                  }}
                >
                  {/* Profile Image */}
                  <img
                    src={profile.image}
                    alt={name}
                    className="w-36 h-36 object-cover mb-4 rounded-full mx-auto cursor-pointer" // Added cursor-pointer for better UX
                    onClick={() => handleProfileImageClick(`${process.env.PUBLIC_URL}${profile.resume}`)} // Open resume when clicked
                  />
                  {/* Team member's name */}
                  <h3 className="text-[#ffcc70] text-xl font-bold mb-2">{name}</h3>
                  {/* Render the role */}
                  <p className="text-gray-300 mb-2">
                    <strong>Role:</strong> {profile.role.join(", ")}
                  </p>
                  {/* Render the education */}
                  <p className="text-gray-300 mb-2">
                    <strong>Education:</strong>
                  </p>
                  <ul className="text-gray-300 list-disc list-inside">
                    {profile.education.map((edu, eduIdx) => (
                      <li key={eduIdx}>{edu}</li>
                    ))}
                  </ul>
                  {/* Render the skills */}
                  <p className="text-gray-300 mt-2">
                    <strong>Skills:</strong>
                  </p>
                  <ul className="text-gray-300 list-disc list-inside">
                    {profile.skills.map((skill, skillIdx) => (
                      <li key={skillIdx}>{skill}</li>
                    ))}
                  </ul>
                  {/* Resume Link */}
                  {profile.resume ? (
                    <a
                      href={`${process.env.PUBLIC_URL}${profile.resume}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-[#ffcc70] hover:underline mt-3 block"
                    >
                      View Resume
                    </a>
                  ) : (
                    <p className="text-gray-400 mt-3 italic text-sm">Resume not available</p>
                  )}
                  {/* Social Media Links */}
                  <div className="flex gap-4 mt-4 justify-center">
                    {profile.facebook && (
                      <a
                        href={profile.facebook}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-[#ffcc70] hover:underline"
                      >
                        Facebook
                      </a>
                    )}
                    {profile.instagram && (
                      <a
                        href={profile.instagram}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-[#ffcc70] hover:underline"
                      >
                        Instagram
                      </a>
                    )}
                  </div>
                  {/* Make card content scrollable */}
                  <div className="max-h-[300px] overflow-auto mt-4">
                    {/* Render more content inside scrollable area */}
                    <p className="text-gray-300">More detailed content can go here.</p>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
}

export default TeamSection;
