import React from "react";
import TeamSection from "./components/TeamSection";

function App() {
  const profiles = {
    "Shaun Russelle Obsenares": {
      image: "/img/shaun russelle obsenares.jpg",
      role: ["Project Manager"],
      education: ["Imus Pilot Elementary School (2010 - 2016)","Bacoor National High School (2016 - 2020)","Victorious Christian Montessori College Inc. (2020 - 2022)","BS in Information Technology - Cavite State University- Imus Campus (2022 - Present)"],
      skills: ["Photoshop", "Java", "Php", "Github"], 
      resume: "/resume/shaun.pdf",
    },
    "Kate Justine Pades": {
      image: "/img/kate justine pades.jpg",
      role: ["Designer"],
      education: ["Bukandala Elementary School (2010 - 2016)","Imus National High School (2016 - 2020)","University of Perpetual Help - Molino Campus (2020 - 2022)","BS in Information Technology - Cavite State University- Imus Campus (2022 - Present)"], 
      skills: ["Canva", "CSS", "HTML"],
      resume: "/resume/pades.pdf",
    },
    "Cristel Nicole Vergara": {
      image: "/img/cristel nicole vergara.jpg",
      role: ["Frontend Developer"],
      education: ["Divine Jesus Learning Center Inc. (2010 - 2016)","Divine Jesus Learning Center Inc. (2016 - 2020)","Emilio Aguinaldo College - Cavite Campus (2020 - 2022)","BS in Information Technology - Cavite State University- Imus Campus (2022 - Present)"], 
      skills: ["Node.js", "Express", "MongoDB"],
      resume: "/resume/cristel.pdf",
    },
    "Cherly Vic Bakilid": {
      image: "/img/cherly vic bakilid.jpg",
      role: ["QA Tester"],
      education: ["New Clarin Elementary School (2010 - 2016)","New Clarin National High School (2016 - 2020)","Global System Institute of Technology (2020 - 2022)","BS in Information Technology - Cavite State University- Imus Campus (2022 - Present)"],
      skills: ["HTML", "CSS", "JavaScript"],
      resume: "/resume/che.pdf",
    },
    "Jhon Mark Morales": {
      image: "/img/jhon mark morales.jpg",
      role: ["QA Tester"],
      education: ["Salawag Elementary School (2007 - 2013)","Paliparan National High School (2013 - 2017)","   University of Perpetual Help - Molino Campus (2017 - 2019)","BS in Information Technology - Cavite State University- Imus Campus (2019 - Present)"], 
      skills: ["React Native", "Flutter"],
      resume: "/resume/jm.pdf",
    },
  };

  const images = [
    { src: "/img/shaun russelle obsenares.jpg", name: "Shaun Russelle Obsenares" },
    { src: "/img/kate justine pades.jpg", name: "Kate Justine Pades" },
    { src: "/img/cristel nicole vergara.jpg", name: "Cristel Nicole Vergara" },
    { src: "/img/cherly vic bakilid.jpg", name: "Cherly Vic Bakilid" },
    { src: "/img/jhon mark morales.jpg", name: "Jhon Mark Morales" },
  ];

  const projects = [
    { title: "UYY BINALOT", desc: "A POS system designed for restaurants...", img: "/img/binalot.png", link: "/projects/Binalots/Binalots/home.html" },
    { title: "KJE PRINTING", desc: "A web app for home-based printing...", img: "/img/kje.jpg", link: "/projects/kje/admin/index.php" },
    { title: "Mobile App", desc: "A mobile app using React Native and Expo.", img: "/img/project3.jpg", link: "#" },
  ];

  const getIdFromName = (name) => name.toLowerCase().replace(/\s+/g, '-');

  return (
    <div>
      {/* Navbar */}
      <nav className="w-full py-5 text-center sticky top-0 bg-[#0f0f0f] z-50">
        <ul className="flex justify-center gap-10 list-none m-0 p-0">
          <li><a href="#portfolio" className="text-white hover:text-gray-400 transition">Portfolio</a></li>
          <li><a href="#about" className="text-white hover:text-gray-400 transition">About</a></li>
          <li><a href="#projects" className="text-white hover:text-gray-400 transition">Projects</a></li>
          <li><a href="#team" className="text-white hover:text-gray-400 transition">Team</a></li>
          <li><a href="#contact" className="text-white hover:text-gray-400 transition">Contact</a></li>
        </ul>
      </nav>

      {/* Portfolio Section */}
      <div id="portfolio" className="flex flex-col items-center mt-16">
  <h1 className="text-6xl tracking-wider mb-10">PORTFOLIO</h1>
  <div className="flex flex-wrap gap-10 justify-center">
    {images.map((img, idx) => {
      // Generate random delay and duration for each image
      const randomDelay = `${Math.random() * 2}s`; // Random delay between 0s and 2s
      const randomDuration = `${2 + Math.random() * 2}s`; // Random duration between 2s and 4s

      return (
        <div key={idx} className="relative flex flex-col items-center">
          <div
            className="w-40 h-52 border-8 border-[#333] shadow-lg bg-white relative animate-swing origin-top"
            style={{
              animationDelay: randomDelay,
              animationDuration: randomDuration,
            }}
          >
            <a href={`#${getIdFromName(img.name)}`} className="w-full h-full">
              <img src={img.src} alt={img.name} className="w-full h-full object-cover" />
            </a>
          </div>
          <a href={`#${getIdFromName(img.name)}`} className="mt-3 text-[#ffcc70] font-bold">{img.name}</a>
        </div>
      );
    })}
  </div>
</div>
      {/* About Section */}
      <section id="about" className="relative py-20 px-[10%] bg-[#0f0f0f] flex flex-col md:flex-row items-center">
  <img 
    src="/img/logo.jpg" 
    alt="Logo" 
    className="absolute left-10 top-1/2 transform -translate-y-1/2 w-80 h-80 object-contain z-10" 
  />
  <div className="ml-[25%] text-center md:text-left">
    <h2 className="text-4xl text-[#ffcc70] mb-5">About Us</h2>
    <p className="text-gray-300">
      Welcome to our portfolio! We are a team of passionate developers and designers dedicated to creating innovative solutions.
    </p>
  </div>
</section>

      {/* Project Section */}
      <section id="projects" className="py-20 px-[10%] bg-[#0f0f0f]">
        <h2 className="text-4xl text-[#ffcc70] text-center mb-10">Projects</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {projects.map((proj, i) => (
            <div key={i} className="bg-[#1a1a1a] border border-[#333] rounded-lg overflow-hidden relative transition-transform duration-300 hover:scale-105 hover:shadow-[0_8px_16px_rgba(255,204,112,0.3)]">
              <a href={proj.link} target="_blank" rel="noopener noreferrer" className="absolute inset-0 z-10 opacity-0"></a>
              <img src={proj.img} alt={proj.title} className="w-full h-44 object-cover" />
              <div className="p-5">
                <h3 className="text-[#ffcc70] mb-2">{proj.title}</h3>
                <p className="text-sm text-gray-300">{proj.desc}</p>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Team Section */}
      <section id="team">
        <TeamSection profiles={profiles} />
      </section>

      {/* Contact Section */}
      <section id="contact" className="bg-[#111] text-center py-20 px-[10%]">
        <h2 className="text-4xl text-[#ffcc70] mb-10">Contact</h2>
        <p className="text-gray-300 mb-2">
          Email: <a href="mailto:ic.admin@cvsu.edu.ph" className="text-[#ffcc70] hover:underline">ic.admin@cvsu.edu.ph</a>
        </p>
        <p className="text-gray-300 mb-2">
          Phone: <a href="tel:+12364370" className="text-[#ffcc70] hover:underline">0912345678</a>
        </p>
        <p className="text-gray-300">
          Follow us on <a href="#" className="text-[#ffcc70] hover:underline">LinkedIn</a> | <a href="#" className="text-[#ffcc70] hover:underline">GitHub</a>
        </p>
      </section>
    </div>
  );
}

export default App;
