import React from "react";
import { Link } from "react-router-dom";
import logo from "../assets/img/logo.jpg";

function TeamSection({ profiles }) {
  if (!profiles || Object.keys(profiles).length === 0) {
    return <p className="text-white">No team profiles available.</p>;
  }

  return (
    <section id="team" className="py-20 px-[10%] bg-[#0f0f0f] text-center relative">
      <h2 className="text-3xl text-[#ffcc70] text-center mb-8">Meet the Team</h2>

      {/* Logo positioned at the top-right, smaller size */}
      <div className="absolute top-4 right-4 z-10">
        <Link to="/contact">
          <img
            src={logo}
            alt="Team Logo"
            id="logo"
            className="w-32 h-32 object-cover rounded-full cursor-pointer transition-all duration-300 hover:opacity-80"
          />
        </Link>
      </div>

      <div className="flex justify-center items-center mt-16 gap-6">
        {/* Container for profile cards */}
        {Object.entries(profiles).map(([name, profile], idx) => (
          <div
            key={idx}
            className="bg-[#1a1a1a] border border-[#333] rounded-lg overflow-hidden p-4 text-center"
            style={{ width: "280px", height: "480px" }} // Increased card height
          >
            {/* Image and Resume Link */}
            {profile.resume ? (
              <a
                href={`${process.env.PUBLIC_URL}${profile.resume}`}
                target="_blank"
                rel="noopener noreferrer"
              >
                <img
                  src={profile.image}
                  alt={name}
                  className="w-24 h-24 object-cover mb-4 rounded-full mx-auto hover:opacity-80 transition"
                />
              </a>
            ) : (
              <img
                src={profile.image}
                alt={name}
                className="w-24 h-24 object-cover mb-4 rounded-full mx-auto"
              />
            )}

            <h3 className="text-[#ffcc70] text-lg font-bold mb-2">{name}</h3>
            <p className="text-gray-300 text-xs mb-2">
              <strong>Role:</strong> {profile.role.join(", ")}
            </p>
            <p className="text-gray-300 text-xs mb-2">
              <strong>Education:</strong>
            </p>
            <ul className="text-gray-300 text-xs list-disc list-inside">
              {profile.education.map((edu, i) => (
                <li key={i}>{edu}</li>
              ))}
            </ul>
            <p className="text-gray-300 text-xs mt-2">
              <strong>Skills:</strong>
            </p>
            <ul className="text-gray-300 text-xs list-disc list-inside">
              {profile.skills.map((skill, i) => (
                <li key={i}>{skill}</li>
              ))}
            </ul>
            {/* Social Links */}
            <div className="flex gap-4 mt-4 justify-center text-xs">
              {profile.facebook && (
                <a
                  href={profile.facebook}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-[#ffcc70] hover:underline"
                >
                  Facebook
                </a>
              )}
              {profile.instagram && (
                <a
                  href={profile.instagram}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-[#ffcc70] hover:underline"
                >
                  Instagram
                </a>
              )}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default TeamSection;
