// src/pages/TeamSection.js
import React from "react";

function TeamSection({ profiles }) {
  if (!profiles || Object.keys(profiles).length === 0) {
    return <p className="text-white text-center py-20">No team profiles available.</p>;
  }

  return (
    <section id="team" className="py-16 px-4 md:px-8 bg-[#0f0f0f]">
      <h2 className="text-3xl text-[#ffcc70] text-center mb-8">Meet the Team</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {Object.entries(profiles).map(([name, profile], idx) => (
          <div
            key={idx}
            className="bg-[#1a1a1a] border border-[#333] rounded-lg p-4 flex flex-col items-center max-w-xs mx-auto"
          >
            {/* Circular Image */}
            <img
              src={profile.image}
              alt={name}
              className="w-20 h-20 rounded-full object-cover mb-3"
            />

            {/* Name */}
            <h3 className="text-[#ffcc70] text-lg font-bold mb-1 text-center">{name}</h3>

            {/* Role */}
            <p className="text-gray-300 text-sm mb-3 text-center">
              <strong>Role:</strong> {profile.role.join(", ")}
            </p>

            {/* Education */}
            <details className="w-full mb-2">
              <summary className="text-[#ffcc70] text-sm font-semibold cursor-pointer">
                Education
              </summary>
              <ul className="text-gray-300 list-disc list-inside text-xs mt-1 space-y-1">
                {profile.education.map((edu, i) => (
                  <li key={i}>{edu}</li>
                ))}
              </ul>
            </details>

            {/* Skills */}
            <details className="w-full mb-2">
              <summary className="text-[#ffcc70] text-sm font-semibold cursor-pointer">
                Skills
              </summary>
              <ul className="text-gray-300 list-disc list-inside text-xs mt-1 space-y-1">
                {profile.skills.map((skill, i) => (
                  <li key={i}>{skill}</li>
                ))}
              </ul>
            </details>

            {/* Resume Link */}
            {profile.resume && (
              <a
                href={`${process.env.PUBLIC_URL}${profile.resume}`}
                target="_blank"
                rel="noopener noreferrer"
                className="mt-3 text-sm text-[#ffcc70] hover:text-white"
              >
                View Resume
              </a>
            )}

            {/* Social Links */}
            <div className="mt-3 flex gap-4 text-sm">
              {profile.facebook && (
                <a
                  href={profile.facebook}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-[#ffcc70] hover:text-white"
                >
                  FB
                </a>
              )}
              {profile.instagram && (
                <a
                  href={profile.instagram}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-[#ffcc70] hover:text-white"
                >
                  IG
                </a>
              )}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default TeamSection;
