import React from "react";

function Portfolio({ profiles }) {
  const getIdFromName = (name) => name.toLowerCase().replace(/\s+/g, "-");

  return (
    <div id="portfolio" className="flex flex-col items-center mt-16 px-6 md:px-12">
      {/* Title */}
      <h1 className="text-4xl sm:text-5xl md:text-6xl tracking-wider mb-10 text-center">
        PORTFOLIO
      </h1>
      
      {/* Portfolio Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8 justify-center">
        {Object.keys(profiles).map((name, idx) => (
          <div key={idx} className="relative flex flex-col items-center">
            <a
              href={`#${getIdFromName(name)}`}
              className="w-full h-52 md:h-60 lg:h-72 border-8 border-[#333] shadow-lg bg-white relative animate-swing origin-top"
            >
              <img
                src={profiles[name].image}
                alt={name}
                className="w-full h-full object-cover"
              />
            </a>
            <h3 className="mt-3 text-[#ffcc70] font-bold text-center">{name}</h3>
            <p className="text-gray-300 text-center">{profiles[name].role.join(", ")}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Portfolio;
