import React, { useState } from "react";
import logo from "../assets/img/logo.jpg"; // Import the logo image

function TeamSection({ profiles }) {
  const [dragging, setDragging] = useState(false);
  const [draggedItem, setDraggedItem] = useState(null);
  const [offset, setOffset] = useState({ x: 0, y: 0 });

  // Check if profiles data is available
  if (!profiles || Object.keys(profiles).length === 0) {
    return <p className="text-white">No team profiles available.</p>;
  }

  const handleMouseDown = (e, index) => {
    setDragging(true);
    const { clientX, clientY } = e;
    setDraggedItem(index);
    setOffset({
      x: clientX,
      y: clientY,
    });
  };

  const handleMouseMove = (e) => {
    if (!dragging || draggedItem === null) return;
    const { clientX, clientY } = e;
    const deltaX = clientX - offset.x;
    const deltaY = clientY - offset.y;

    const updatedProfiles = [...Object.entries(profiles)];
    const profile = updatedProfiles[draggedItem][1];

    if (!profile.position) {
      profile.position = { x: 0, y: 0 };
    }

    profile.position.x += deltaX;
    profile.position.y += deltaY;

    profiles[updatedProfiles[draggedItem][0]] = profile;

    setOffset({
      x: clientX,
      y: clientY,
    });
  };

  const handleMouseUp = () => {
    setDragging(false);
    setDraggedItem(null);
  };

  return (
    <section
      id="team"
      className="py-20 px-[10%] bg-[#0f0f0f] relative"
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
    >
      <h2 className="text-4xl text-[#ffcc70] text-center mb-10">Meet the Team</h2>
      <div className="flex justify-center items-center relative">
        {/* Center Logo */}
        <img
          src={logo}
          alt="Team Logo"
          className="w-48 h-48 object-cover rounded-full absolute z-10"
        />
        {/* Profiles around the logo */}
        <div className="relative w-[700px] h-[700px] overflow-hidden"> {/* Added overflow hidden for the container */}
          <div className="absolute inset-0 flex justify-center items-center overflow-x-auto"> {/* Added scrolling */}
            <div className="flex space-x-8"> {/* This ensures horizontal scrolling */}
              {Object.entries(profiles).map(([name, profile], idx) => {
                if (!profile.position) {
                  profile.position = { x: 0, y: 0 };
                }

                // Calculate angle for circular positioning
                const angle = (360 / Object.entries(profiles).length) * idx;
                const transform = `rotate(${angle}deg) translateX(300px) rotate(-${angle}deg)`; // Adjusted for larger card spacing

                return (
                  <div
                    key={idx}
                    className="cursor-pointer"
                    style={{
                      transform: transform,
                      left: `${profile.position.x || 0}px`,
                      top: `${profile.position.y || 0}px`,
                    }}
                    onMouseDown={(e) => handleMouseDown(e, idx)}
                  >
                    <div
                      className="bg-[#1a1a1a] border border-[#333] rounded-lg overflow-hidden p-5 text-center"
                      style={{
                        width: "400px", // Increased card width
                        height: "500px", // Increased card height for more content
                      }}
                    >
                      {/* Render the profile image as a circle */}
                      <img
                        src={profile.image}
                        alt={name}
                        className="w-36 h-36 object-cover mb-4 rounded-full mx-auto"
                      />
                      {/* Render the team member's name */}
                      <h3 className="text-[#ffcc70] text-xl font-bold mb-2">{name}</h3>
                      {/* Render the role */}
                      <p className="text-gray-300 mb-2">
                        <strong>Role:</strong> {profile.role.join(", ")}
                      </p>
                      {/* Render the education */}
                      <p className="text-gray-300 mb-2">
                        <strong>Education:</strong>
                      </p>
                      <ul className="text-gray-300 list-disc list-inside">
                        {profile.education.map((edu, eduIdx) => (
                          <li key={eduIdx}>{edu}</li>
                        ))}
                      </ul>
                      {/* Render the skills */}
                      <p className="text-gray-300 mt-2">
                        <strong>Skills:</strong>
                      </p>
                      <ul className="text-gray-300 list-disc list-inside">
                        {profile.skills.map((skill, skillIdx) => (
                          <li key={skillIdx}>{skill}</li>
                        ))}
                      </ul>
                      {/* Render the resume link if available */}
                      {profile.resume ? (
                        <a
                          href={`${process.env.PUBLIC_URL}${profile.resume}`}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-[#ffcc70] hover:underline mt-3 block"
                        >
                          View Resume
                        </a>
                      ) : (
                        <p className="text-gray-400 mt-3 italic text-sm">Resume not available</p>
                      )}
                      {/* Render social media links */}
                      <div className="flex gap-4 mt-4 justify-center">
                        {profile.facebook && (
                          <a
                            href={profile.facebook}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="text-[#ffcc70] hover:underline"
                          >
                            Facebook
                          </a>
                        )}
                        {profile.instagram && (
                          <a
                            href={profile.instagram}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="text-[#ffcc70] hover:underline"
                          >
                            Instagram
                          </a>
                        )}
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default TeamSection;
