{projects.map((project, index) => (
  <div key={index} className="project-card">
    <img src={project.img} alt={project.title} />
    <h3>{project.title}</h3>
    <p>{project.desc}</p>
    
    {/* If languages exist, display them */}
    {project.languages && (
      <ul className="text-sm text-gray-400 mt-2">
        {project.languages.map((lang, i) => (
          <li key={i}>â€¢ {lang}</li>
        ))}
      </ul>
    )}

    {/* Show video or link */}
    {project.video ? (
      <iframe
        src={project.video}
        width="100%"
        height="200"
        allow="autoplay"
        title={project.title}
      />
    ) : (
      project.link && (
        <a href={project.link} className="text-blue-400 underline" target="_blank" rel="noreferrer">
          Visit Project
        </a>
      )
    )}
  </div>
))}
