function TeamSection({ profiles }) {
  if (!profiles || Object.keys(profiles).length === 0) {
    return <p className="text-white">No team profiles available.</p>;
  }

  return (
    <section className="py-12 px-6 sm:px-12 md:px-16 lg:px-[10%] bg-[#0f0f0f] text-center">
      <h2 className="text-3xl sm:text-2xl md:text-3xl lg:text-4xl text-[#ffcc70] text-center mb-8">
        Meet the Team
      </h2>
      <div className="flex justify-center items-center mt-12 gap-6 flex-wrap">
        {Object.entries(profiles).map(([name, profile], idx) => (
          <div
            key={idx}
            className="bg-[#1a1a1a] border border-[#333] rounded-lg overflow-hidden p-4 text-center w-full sm:w-72 md:w-80 lg:w-96"
          >
            <img
              src={profile.image}
              alt={name}
              className="w-24 h-24 object-cover mb-4 rounded-full mx-auto"
            />
            <h3 className="text-[#ffcc70] text-lg sm:text-base md:text-lg font-bold mb-2">
              {name}
            </h3>
            <p className="text-gray-300 text-xs sm:text-sm mb-2">
              <strong>Role:</strong> {profile.role.join(", ")}
            </p>
            <ul className="text-gray-300 text-xs sm:text-sm list-disc list-inside">
              {profile.education.map((edu, i) => (
                <li key={i}>{edu}</li>
              ))}
            </ul>
            <div className="flex gap-4 mt-4 justify-center text-xs sm:text-sm">
              {profile.facebook && (
                <a
                  href={profile.facebook}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-[#ffcc70] hover:underline"
                >
                  Facebook
                </a>
              )}
              {profile.instagram && (
                <a
                  href={profile.instagram}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-[#ffcc70] hover:underline"
                >
                  Instagram
                </a>
              )}
            </div>
            {/* Resume Link */}
            {profile.resume && (
              <div className="mt-4">
                <a
                  href={profile.resume}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-[#ffcc70] hover:underline"
                >
                  View Resume
                </a>
              </div>
            )}
          </div>
        ))}
      </div>
    </section>
  );
}

export default TeamSection;
