import React from "react";
import { Link } from "react-router-dom";
import logo from "../assets/img/logo.jpg";

function TeamSection({ profiles }) {
  if (!profiles || Object.keys(profiles).length === 0) {
    return <p className="text-white">No team profiles available.</p>;
  }

  return (
    <section id="team" className="py-20 px-[10%] bg-[#0f0f0f] text-center">
      <h2 className="text-4xl text-[#ffcc70] text-center mb-10">Meet the Team</h2>

      <div className="flex justify-center items-center relative">
        {/* Logo linking to Contact */}
        <Link to="/contact" className="absolute z-10">
          <img
            src={logo}
            alt="Team Logo"
            id="logo"
            className="w-48 h-48 object-cover rounded-full cursor-pointer transition-all duration-300 hover:opacity-80"
          />
        </Link>

        {/* Container for profile cards */}
        <div className="flex justify-center gap-8 mt-20">
          {Object.entries(profiles).map(([name, profile], idx) => (
            <div
              key={idx}
              className="bg-[#1a1a1a] border border-[#333] rounded-lg overflow-hidden p-4 text-center"
              style={{ width: "300px", height: "400px" }}  // Reduced size for cards
            >
              {/* Image and Resume Link */}
              {profile.resume ? (
                <a
                  href={`${process.env.PUBLIC_URL}${profile.resume}`}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <img
                    src={profile.image}
                    alt={name}
                    className="w-24 h-24 object-cover mb-4 rounded-full mx-auto hover:opacity-80 transition"
                  />
                </a>
              ) : (
                <img
                  src={profile.image}
                  alt={name}
                  className="w-24 h-24 object-cover mb-4 rounded-full mx-auto"
                />
              )}

              <h3 className="text-[#ffcc70] text-xl font-bold mb-2">{name}</h3>
              <p className="text-gray-300 mb-2">
                <strong>Role:</strong> {profile.role.join(", ")}
              </p>
              <p className="text-gray-300 mb-2">
                <strong>Education:</strong>
              </p>
              <ul className="text-gray-300 list-disc list-inside">
                {profile.education.map((edu, i) => (
                  <li key={i}>{edu}</li>
                ))}
              </ul>
              <p className="text-gray-300 mt-2">
                <strong>Skills:</strong>
              </p>
              <ul className="text-gray-300 list-disc list-inside">
                {profile.skills.map((skill, i) => (
                  <li key={i}>{skill}</li>
                ))}
              </ul>
              {/* Social Links */}
              <div className="flex gap-4 mt-4 justify-center">
                {profile.facebook && (
                  <a
                    href={profile.facebook}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-[#ffcc70] hover:underline"
                  >
                    Facebook
                  </a>
                )}
                {profile.instagram && (
                  <a
                    href={profile.instagram}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-[#ffcc70] hover:underline"
                  >
                    Instagram
                  </a>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default TeamSection;
